var fs = require('fs'),
    musicmetadata = require('./lib/index');

//create a new parser from a node ReadStream
var parser = new musicmetadata(fs.createReadStream('/Users/leetreveil/Downloads/11 - Katatonia - Dead Letters.m4a'));

//listen for the metadata event
parser.on('metadata', function(result) {
  console.log(result);
});

parser.on('done', function (err) {
    if (err) {throw err }
})